import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal from "react-modal";

const GameList = () => {
  const [listGames, setListGames] = useState(null);
  const [bestGame, setBestGame] = useState("");
  const [allGames, setAllGame] = useState("");
  const [modalGameIsOpen, setModalGameIsOpen] = useState(false);
  const [moreInfos, setMoreInfos] = useState(null);
  const [idScreenshots, setIdScreenshots] = useState(0);
  useEffect(() => {
    axios.get("https://wild-games.herokuapp.com/api/v1").then((response) => {
      setListGames(response.data.map((item) => item));
      setBestGame(response.data.filter((note) => note.rating >= 4.5));
      setAllGame(response.data.map((item) => item));
      setMoreInfos(response.data.map((item) => item.short_screenshots));
    });
  }, []);
  return listGames !== null && moreInfos !== null ? (
    <div className="App">
      <button onClick={() => setListGames(bestGame)}>Best Games</button>
      <button onClick={() => setListGames(allGames)}>All Games</button>
      {listGames.map((item) => {
        return (
          <div className="listPicture">
            <Modal
              isOpen={modalGameIsOpen}
              style={{
                content: {
                  backgroundColor: "black",
                },
              }}
            >
              <button
                style={{
                  width: "10%",
                  height: "40px",
                  fontSize: "large",
                  fontWeight: "bolder",
                  marginLeft: "10px",
                  marginBottom: "10px",
                  marginTop: "10px",
                }}
                onClick={() => setModalGameIsOpen(false)}
              >
                hide the modal
              </button>
              <button  style={{
                  width: "10%",
                  height: "40px",
                  fontSize: "large",
                  fontWeight: "bolder",
                  marginLeft: "10px",
                  marginBottom: "10px",
                  marginTop: "10px",
                }} onClick = {() => setIdScreenshots(idScreenshots + 1)}> + Screenshots

              </button>
              <div>
                {moreInfos.map((item) => {
                  return (
                    <div>
                      <img style = {{width: "80%", marginLeft: "10%"}} src={item[idScreenshots].image} />
                    </div>
                  );
                })}
              </div>
              <button
                style={{
                  width: "10%",
                  height: "40px",
                  fontSize: "large",
                  fontWeight: "bolder",
                  marginLeft: "10px",
                  marginBottom: "10px",
                  marginTop: "10px",
                }}
                onClick={() => setModalGameIsOpen(false)}
              >
                hide the modal
              </button>
              <button  style={{
                  width: "10%",
                  height: "40px",
                  fontSize: "large",
                  fontWeight: "bolder",
                  marginLeft: "10px",
                  marginBottom: "10px",
                  marginTop: "10px",
                }} onClick = {() => setIdScreenshots(idScreenshots + 1)}> + Screenshots

              </button>
            </Modal>
            <div
              style={{
                border: "2px solid black",
                width: "90%",
                marginBottom: "30px",
                backgroundColor: "gray",
              }}
            >
              <p>Name : {item.name}</p>
              <p>Release : {item.released}</p>
              <img src={item.background_image} alt="picture-game" />
              <p>Evaluation : {item.rating}/5</p>
              <label for="file"></label>

              <progress id="file" max="5" value={item.rating}>
                {" "}
              </progress>
              <button onClick={() => setModalGameIsOpen(true)}>Screenshots</button>
            </div>
          </div>
        );
      })}

      <button onClick={() => setListGames(bestGame)}>Best Games</button>
      <button onClick={() => setListGames(allGames)}>All Games</button>
    </div>
  ) : (
    <p>Pas de data</p>
  );
};

export default GameList;
