import React, { useState } from "react";
import Modal from "react-modal";
import linkedin from "../images/linkedin.png";
import github from "../images/github.png";
import email from "../images/email.png";
import phone from "../images/phone.png";

const Footer = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  return (
    <div className="footer">
      <Modal
        isOpen={modalIsOpen}
        style={{
          content: {
            backgroundColor: "black",
          },
        }}
      >
        <button
          onClick={() => setModalIsOpen(false)}
          style={{
            width: "10%",
            height: "40px",
            fontSize: "large",
            fontWeight: "bolder",
            marginLeft: "10px",
            marginBottom: "30px",
            marginTop: "10px",
            color: "black",
            border: "solid 5px wheat",
            boxShadow: "0px 0px 20px #00ccff",
            inset: "0px 0px 20px #00ccff",
            inset: "0px 0px 60px #00ffff",
          }}
        >
          hide the modal
        </button>
        <div>
          <h1>Name : STAMBOULI AMINE</h1>
          <a
            href="https://www.linkedin.com/in/stambouli-d%C3%A9veloppeur/"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img src={linkedin} style={{ width: "5%" }} />
            <button
              style={{
                width: "10%",
                height: "40px",
                fontSize: "large",
                fontWeight: "bolder",
                marginLeft: "10px",
                marginBottom: "30px",
                marginTop: "10px",
                color: "black",
                border: "solid 5px wheat",
                boxShadow: "0px 0px 20px #00ccff",
                inset: "0px 0px 20px #00ccff",
                inset: "0px 0px 60px #00ffff",
              }}
            >
              My Linkedin
            </button>
          </a>
          <a
            href="https://github.com/STAM0283"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img src={github} style={{ width: "5%" }} />
            <button
              style={{
                width: "10%",
                height: "40px",
                fontSize: "large",
                fontWeight: "bolder",
                marginLeft: "10px",
                marginBottom: "30px",
                marginTop: "10px",
                color: "black",
                border: "solid 5px wheat",
                boxShadow: "0px 0px 20px #00ccff",
                inset: "0px 0px 20px #00ccff",
                inset: "0px 0px 60px #00ffff",
              }}
            >
              My GitHub
            </button>
          </a>
          <img src={email} style={{ width: "5%" }} />
          <h1>E-mail : aminestambouli00780@gmail.com</h1>
          <img src={phone} style={{ width: "5%" }} />
          <h1>Phone Number : 06 99 06 93 90</h1>
        </div>
      </Modal>
      <button class="btn btn-outline-primary btn-lg"
        onClick={() => setModalIsOpen(true)}
        style={{
          width: "10%",
          height: "40px",
          fontSize: "large",
          fontWeight: "bolder",
          marginLeft: "10px",
          marginBottom: "30px",
          marginTop: "10px",
        }}
      >
        CONTACT
      </button>
    </div>
  );
};

export default Footer;
